.badge-info {
  background: #000;
  color: #fff !important;
}

.badge-warning {
  background: orange;
  color: #fff;
}

.badge-success {
  background: #16ce8e;
  color: #fff;
}

.badge-top {
  position: absolute;
  border-radius: 50%;
  background: orange;
  line-height: 20px;
  width: 20px;
  height: 20px;
  top: -15%;
  right: 3%;
  padding-left: 2px;
  -webkit-box-shadow: 2px 2px 5px 0px grey;
  -moz-box-shadow: 2px 2px 5px 0px grey;
  box-shadow: 2px 2px 5px 0px grey;
}

.alert-info,
.alert-warning {
  border: 0px;
  font-family: Verdana, Tahoma, Arial;
  font-size: 0.9em;
}

.wrap-text {
  white-space: nowrap;
  width: 100%;
  overflow: hidden;
  text-overflow: ellipsis;
}

.circle {
  width: 40px;
  height: 40px;
  border-radius: 50px;
  padding-top: 12px;
  outline: none !important;
  font-weight: bolder;
}

.circle > i.fa-2x {
