function pageprocessorAccTranscript(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);


  $rootScope.getAPI(pagedef.dropdownendpoint, function(data) {
    if (data.blockTranscriptsGrades) {
      if (data.blockTranscriptsGrades.length > 0) {
        if (data.blockTranscriptsGrades[0].BLOCKTRANSCRIPTS == 'Y' || data.blockTranscriptsGrades[0].BLOCKGRADES == 'Y') {
          if (window.device) {
            var doNothing = function() {
              $rootScope.$apply(function() {
                $location.path("/home");
              });
            };
            navigator.notification.alert("Your transcript is not available due to holds on your record.", doNothing, 'Alert', 'Ok');
          } else {
            apprise("Your transcript is not available due to holds on your record.", {
              'verify': false,
              'textYes': "Ok"
            }, function(r) {
              $rootScope.$apply(function() {
                $location.path("/home");
              });
            });
          }
        } else {
          $scope.dropdown = data;
          if ($scope.dropdown.transcripttypedropdown.length > 0) {
            $scope.creditselectedvalue = $scope.dropdown.transcripttypedropdown[0].TRANSCRIPT_CODE;
            $('#credittype').blur();
          }
          $scope.$apply();

        }
      } else {
        $scope.dropdown = data;
        if ($scope.dropdown.transcripttypedropdown.length > 0) {
          $scope.creditselectedvalue = $scope.dropdown.transcripttypedropdown[0].TRANSCRIPT_CODE;
          $('#credittype').blur();
        }
        $scope.$apply();
      }
    } else {
      $scope.dropdown = data;
      if ($scope.dropdown.transcripttypedropdown.length > 0) {
        $scope.creditselectedvalue = $scope.dropdown.transcripttypedropdown[0].TRANSCRIPT_CODE;
        $('#credittype').blur();
      }
      $scope.$apply();
    }

  });

  $scope.dropdownselect = function(selectedvalue) {

    $rootScope.getAPI(pagedef.listendpoint + $scope.selectedvalue, function(data) {
      $scope.listdata = data;
      $scope.creditselect($scope.creditselectedvalue);
      $('#level').blur();
    });
  }
  $scope.creditselect = function(selectedvalue) {
    $scope.transfer = null;
    $scope.institution = null;
    $scope.transcripts = null;
    // if ($scope.creditselectedvalue == "transfer") {
    var transfer = {
      attempted: 0,
      passed: 0,
      earned: 0,
      gpa: 0,
      credit: 0
    };
    transfer.attempted = angular.isUndefined($scope.listdata.transfer_data_total[0][0].ATTEMPTED_HOURS) ? 0 : $scope.listdata.transfer_data_total[0][0].ATTEMPTED_HOURS;
    transfer.passed = angular.isUndefined($scope.listdata.transfer_data_total[0][0].PASSED_HOURS) ? 0 : $scope.listdata.transfer_data_total[0][0].PASSED_HOURS;
    transfer.earned = angular.isUndefined($scope.listdata.transfer_data_total[0][0].EARNED_HOURS) ? 0 : $scope.listdata.transfer_data_total[0][0].EARNED_HOURS;
    transfer.gpa = angular.isUndefined($scope.listdata.transfer_data_total[0][0].GPA_HOURS) ? 0 : $scope.listdata.transfer_data_total[0][0].GPA_HOURS;
    transfer.credit = angular.isUndefined($scope.listdata.transfer_data_total[0][0].QUALITY_POINTS) ? 0 : $scope.listdata.transfer_data_total[0][0].QUALITY_POINTS;
    $scope.transfer = transfer;
    // } else if ($scope.creditselectedvalue == "institution") {
    $scope.institution = "data";
    var totalinst = {
      ah: 0,
      ph: 0,
      eh: 0,
      gpah: 0,
      qp: 0
    };
    angular.forEach($scope.listdata.institutioncredits, function(value, key) {
      this.ah += parseFloat(value.ATTEMPTED_HOURS);
      this.ph += parseFloat(value.SHRTCKG_PASS_HOURS);
      this.eh += parseFloat(value.SHRTCKG_EARN_HOURS);
      this.gpah += parseFloat(value.SHRTCKG_GPA_HOURS);
      this.qp += parseFloat(value.SHRTCKG_QUAL_PTS);
    }, totalinst);
    $scope.insttotal = totalinst;

    // } else if ($scope.creditselectedvalue == "transcripts") {
    $scope.transcripts = "data";
    angular.forEach($scope.listdata.transcripttotal, function(value, key) {
      if (value.TRANSCRIPT_TYPE == "I") {
        $scope.totalinst = value;
      } else if (value.TRANSCRIPT_TYPE == "T") {
        $scope.totaltrans = value;
      } else if (value.TRANSCRIPT_TYPE == "Z") {
        $scope.cumulative = value;
      }
    });
    // }

  }
}

function pageprocessorAddUpdateB1Details(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  var curDate = new Date();
  var months = ["JAN", "FEB", "MAR", "APR", "MAY", "JUN", "JUL", "AUG", "SEP", "OCT", "NOV", "DEC"];
  var formattedCurDate = (curDate.getDate() < 10 ? '0' + curDate.getDate() : '' + curDate.getDate()) + "-" + months[curDate.getMonth()] + "-" + curDate.getFullYear();

  /*$(".datepicker").pickadate({
      format: "dd-mmm-yyyy",
      formatSubmit: "yyyy/mm/dd",
      editable: true,
      onSet: function (context) {
          $scope.address.FROM_DATE = $("#fromDate").val();
          $scope.address.TO_DATE = $("#toDate").val();
      }
  });*/
  $scope.dropdownselect = function() {
    var selectedAddrType = $scope.address.ADDRESS_TYPE_CODE;
    if (selectedAddrType == "" || $scope.add == false) {
      return;
    }
    var found = false;
    $.each($rootScope.addresses.addresses, function(i, val) {
      if (val.ADDRESS_TYPE_CODE == selectedAddrType) {
        found = true;
      }
    });

    if (found) {
      $scope.address.ADDRESS_TYPE_CODE = "";
      $scope.alert("An address exists for this type. Please select a different address type or go back and edit the existing one.", "Address Exists");
    }
  }
  $scope.addAdditional = function() {
    $scope.address.additionalphones.push({
      "PHONE_SEQ": "",
      "PHONE_CODE": "",
      "COUNTRY_CODE": "",
      "AREA_CODE": "",
      "PHONE_NUMBER": "",
      "EXTENSION": "",
      "INTERNATIONAL_ACCESS": "",
      "UNLISTED_IND": "",
      "visible": true
    });
  }
  var index = $routeParams.index;
  var cindex = $routeParams.cindex;
  if (cindex) {
    $scope.title = "Edit Contact Number";
    $scope.add = false;
    $scope.address = $rootScope.addresses.addresses[$routeParams.index];
    if (cindex != -1) {
      $scope.address.additionalphones[cindex].visible = true;
    } else {
      $scope.addAdditional();
    }

  } else if (index == -1) {

    $scope.title = "Add New Address";
    $scope.add = true;
    $scope.address = {
      "ADDRESS_TYPE_CODE": "",
      "HOUSE_NUMBER": "",
      "STREET_LINE1": "",
      "STREET_LINE2": "",
      "STREET_LINE3": "",
      "STREET_LINE4": "",
      "CITY": "",
      "STATE_CODE": "",
      "ZIP": "",
      "COUNTY_CODE": null,
      "NATION_CODE": "",
      "FROM_DATE": formattedCurDate,
      "TO_DATE": "",
      primaryphone: {
        "COUNTRY_CODE": "",
        "AREA_CODE": "",
        "PHONE_NUMBER": "",
        "EXTENSION": "",
        "INTERNATIONAL_ACCESS": "",
        "UNLISTED_IND": ""
      },
      additionalphones: []
    }

  } else {
    $scope.title = "Edit Address";
    $scope.add = true;
    $scope.editaddress = true;
    $scope.address = $rootScope.addresses.addresses[$routeParams.index];
    $scope.address.FROM_DATE = formattedCurDate;
    $scope.address.TO_DATE = "";
    angular.forEach($scope.address.additionalphones, function(value, key) {
      value.visible = true;
    });
  }
  $scope.alert = function(message, title) {
    if (window.device) {
      navigator.notification.alert(message, null, title, null);
    } else {
      apprise(message, {
        'verify': false,
        'textYes': "Ok"
      }, function(r) {});
    }
  }

  $scope.resetContact = function() {
    $rootScope.$apply(function() {
      $location.path("/app/AddUpdateB1/AddUpdateB1");
    });
  }

  $scope.validateAddress = function() {
    //Validate city
    var city = $scope.address.CITY == null ? "" : $scope.address.CITY;
    /*if (!(city.search(/[^a-z. A-Z. ]+/) === -1)) {
        $scope.alert("Only alphabets and periods are allowed in City", "Address");
        return false;
    }*/
    if (city == "" || city.trim() == "") {
      $scope.alert("Please enter City", "Address");
      return false;
    }
    if ($scope.address.STREET_LINE1 == "" || $scope.address.STREET_LINE1 == null) {
      $scope.alert("Please enter Address line 1", "Address");
      return false;
    }
    var zip = $scope.address.ZIP == null ? "" : $scope.address.ZIP.trim();
    if ($scope.address.NATION_CODE == "" && zip == "" && $scope.address.STATE_CODE == "") {
      $scope.alert("State and Zip or Nation is required.", "Address");
      return false;
    }
    if (zip == "" && $scope.address.STATE_CODE != "") {
      $scope.alert("State and Zip or Nation is required.", "Address");
      return false;
    }
    if ($scope.address.NATION_CODE == "zz" && zip == "" && $scope.address.STATE_CODE == "") {
      $scope.alert("State and Zip is required.", "Address");
      return false;
    }
    if ($scope.address.primaryphone) {
      if ($scope.address.primaryphone.PHONE_NUMBER == '' || $scope.address.primaryphone.PHONE_NUMBER == null) {
        $scope.alert("Phone number is required.", "Address");
        return false;
      }
    }
    var haveError = false;
    angular.forEach($scope.address.additionalphones, function(value, key) {
      if (value.PHONE_CODE == '' || value.PHONE_CODE == null) {
        haveError = true;
        $scope.alert("Telephone Type is required.", "Address");
        return false;
      } else if (value.PHONE_NUMBER == '' || value.PHONE_NUMBER == null) {
        haveError = true;
        $scope.alert("Telephone Number is required.", "Address");
        return false;
      }
    });
    if (haveError) {
      return false;
    }

    return true
  }


  $scope.addAddress = function() {
    if ($scope.validateAddress() == false) {
      return;
    }
    var postdata = "";
    if (!$scope.add || $scope.editaddress == true) {
      postdata += "seqno=" + $scope.address.ADDRESS_SEQ + "&";
    }
    postdata += "atypcode=" + $scope.address.ADDRESS_TYPE_CODE + "&";
    postdata += "fromdate=" + ($scope.address.FROM_DATE == null ? "" : $scope.address.FROM_DATE) + "&";
    postdata += "todate=" + ($scope.address.TO_DATE == null ? "" : $scope.address.TO_DATE) + "&";
    postdata += "housenumber=" + ($scope.address.HOUSE_NUMBER == null ? "" : $scope.address.HOUSE_NUMBER) + "&";
    postdata += "streetline1=" + ($scope.address.STREET_LINE1 == null ? "" : $scope.address.STREET_LINE1) + "&";
    postdata += "streetline2=" + ($scope.address.STREET_LINE2 == null ? "" : $scope.address.STREET_LINE2) + "&";
    postdata += "streetline3=" + ($scope.address.STREET_LINE3 == null ? "" : $scope.address.STREET_LINE3) + "&";
    postdata += "streetline4=" + ($scope.address.STREET_LINE4 == null ? "" : $scope.address.STREET_LINE4) + "&";
    postdata += "city=" + ($scope.address.CITY == null ? "" : $scope.address.CITY) + "&";
    postdata += "statecode=" + ($scope.address.STATE_CODE == null ? "" : $scope.address.STATE_CODE) + "&";
    postdata += "countycode=" + ($scope.address.COUNTY_CODE == null ? "" : $scope.address.COUNTY_CODE) + "&";
    postdata += "nationcode=" + ($scope.address.NATION_CODE == null ? "" : $scope.address.NATION_CODE) + "&";
    postdata += "zip=" + ($scope.address.ZIP == null ? "" : $scope.address.ZIP) + "&";
    if ($scope.address.primaryphone) {
      postdata += "phoneareacode=" + ($scope.address.primaryphone.AREA_CODE == null ? "" : $scope.address.primaryphone.AREA_CODE) + "&";
      postdata += "phonenumber=" + ($scope.address.primaryphone.PHONE_NUMBER == null ? "" : $scope.address.primaryphone.PHONE_NUMBER) + "&";
      postdata += "phoneextn=" + ($scope.address.primaryphone.EXTENSION == null ? "" : $scope.address.primaryphone.EXTENSION) + "&";
      postdata += "phonecountrycode=" + ($scope.address.primaryphone.COUNTRY_CODE == null ? "" : $scope.address.primaryphone.COUNTRY_CODE) + "&";
      postdata += "intlaccess=" + ($scope.address.primaryphone.INTERNATIONAL_ACCESS == null ? "" : $scope.address.primaryphone.INTERNATIONAL_ACCESS) + "&";
      postdata += "unlistedind=" + ($scope.address.primaryphone.UNLISTED_IND == null ? "" : $scope.address.primaryphone.UNLISTED_IND) + "&";
    } else {
      postdata += "phoneareacode=" + "" + "&";
      postdata += "phonenumber=" + "" + "&";
      postdata += "phoneextn=" + "" + "&";
      postdata += "phonecountrycode=" + "" + "&";
      postdata += "intlaccess=" + "" + "&";
      postdata += "unlistedind=" + "" + "&";
    }

    var addlseqnos = "";
    var addltelecodes = "";
    var addlcountrycodes = "";
    var addlareacodes = "";
    var addlphonenumbers = "";
    var addlphoneextns = "";
    var addlintlaccess = "";
    var addlunlistedinds = "";
    var addldelinds = "";

    angular.forEach($scope.address.additionalphones, function(value, key) {
      if (key == 0) {
        addlseqnos += (value.PHONE_SEQ == null ? "" : value.PHONE_SEQ);
        addltelecodes += (value.PHONE_CODE == null ? "" : value.PHONE_CODE);
        addlcountrycodes += (value.COUNTRY_CODE == null ? "" : value.COUNTRY_CODE);
        addlareacodes += (value.AREA_CODE == null ? "" : value.AREA_CODE);
        addlphonenumbers += (value.PHONE_NUMBER == null ? "" : value.PHONE_NUMBER);
        addlphoneextns += (value.EXTENSION == null ? "" : value.EXTENSION);
        addlintlaccess += (value.INTERNATIONAL_ACCESS == null ? "" : value.INTERNATIONAL_ACCESS);
        addlunlistedinds += (value.UNLISTED_IND == null ? "" : value.UNLISTED_IND);
        addldelinds += "";
      } else {
        addlseqnos += "," + (value.PHONE_SEQ == null ? "" : value.PHONE_SEQ);
        addltelecodes += "," + (value.PHONE_CODE == null ? "" : value.PHONE_CODE);
        addlcountrycodes += "," + (value.COUNTRY_CODE == null ? "" : value.COUNTRY_CODE);
        addlareacodes += "," + (value.AREA_CODE == null ? "" : value.AREA_CODE);
        addlphonenumbers += "," + (value.PHONE_NUMBER == null ? "" : value.PHONE_NUMBER);
        addlphoneextns += "," + (value.EXTENSION == null ? "" : value.EXTENSION);
        addlintlaccess += "," + (value.INTERNATIONAL_ACCESS == null ? "" : value.INTERNATIONAL_ACCESS);
        addlunlistedinds += "," + (value.UNLISTED_IND == null ? "" : value.UNLISTED_IND);
        addldelinds += ",";
      }

    });
    if (!$scope.add) {
      postdata += "addlseqnos=" + addlseqnos + "&";
    }

    postdata += "addltelecodes=" + addltelecodes + "&";
    postdata += "addlcountrycodes=" + addlcountrycodes + "&";
    postdata += "addlareacodes=" + addlareacodes + "&";
    postdata += "addlphonenumbers=" + addlphonenumbers + "&";
    postdata += "addlphoneextns=" + addlphoneextns + "&";
    postdata += "addlintlaccess=" + addlintlaccess + "&";
    postdata += "addlunlistedinds=" + addlunlistedinds + "&";
    postdata += "addldelinds=" + addldelinds;

    var endpoint =  pagedef.endpoint;
    if (!$scope.add || $scope.editaddress == true) {
      endpoint =  "/banneraddressupdate";
    }

    $rootScope.postAPI(endpoint, postdata, function(data) {
      if (data) {
        apprise("Address saved successfully", {
          'verify': false,
          'textYes': "Ok"
        }, function(r) {
          $rootScope.$apply(function() {
            $location.path("/app/AddUpdateB1/AddUpdateB1");
          });
        });
      }
    });
  }
}

function pageprocessorAddUpdateB1(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  $scope.showButton = false;
  $.blockUI();

  $scope.fetchaddr = function() {
    $rootScope.getAPI( "/banneraddressfetch", function(data) {

      var addresses = {
        addresses: []
      };
      angular.forEach(data.addresses, function(addr, key) {
        var address = addr;
        angular.forEach(data.primaryphones, function(phone, key) {
          if (phone.ADDRESS_SEQ == address.ADDRESS_SEQ && phone.ADDRESS_TYPE_CODE == address.ADDRESS_TYPE_CODE) {
            address.primaryphone = phone;
          }
        });

        address.additionalphones = [];
        angular.forEach(data.additionalphones, function(addphone, key) {
          if (addphone.ADDRESS_SEQ == address.ADDRESS_SEQ && addphone.ADDRESS_TYPE_CODE == address.ADDRESS_TYPE_CODE) {
            addphone.toggle = false;
            addphone.toggleclass = "fa-chevron-right";
            addphone.delindicator = "";
            address.additionalphones.push(addphone);
          }
        });
        addresses.addresses.push(address);

      });

      $scope.addressdata = data;
      $scope.addresses = addresses;
      $rootScope.addresses = addresses;
      //call the second service


    });
    $scope.showButton = true;
    $.unblockUI();
  }

  $rootScope.getAPI( pagedef.endpoint, function(data) {
    $scope.addresscodes = data;
    $rootScope.telephonetypecodes = data.telephonetypecodes;
    $rootScope.nationcodes = data.nationcodes;
    $rootScope.statecodes = data.statecodes;
    $rootScope.addresstypecodes = data.addresstypecodes
      //$rootScope.countycodes = data.countycodes;
    $scope.fetchaddr();
    $scope.getTelephoneType = function(code) {
      var teletype = "";
      angular.forEach($scope.addresscodes.telephonetypecodes, function(value, key) {
        if (value.TELE_CODE == code) {
          teletype = value.TELE_DESC;
        }
      });
      return teletype;
    }

    $scope.getNation = function(code) {
      var nation = "";
      angular.forEach($scope.addresscodes.nationcodes, function(value, key) {
        if (value.NATION_CODE == code) {
          nation = value.NATION_DESC;
        }
      });
      return nation;
    }
    $scope.getState = function(code) {
      var state = "";
      angular.forEach($scope.addresscodes.statecodes, function(value, key) {
        if (value.STATE_CODE == code) {
          state = value.STATE_DESC;
        }
      });
      return state;
    }
    $scope.getAddressType = function(code) {
      var rel = "";
      angular.forEach($scope.addresscodes.addresstypecodes, function(value, key) {
        if (value.ADDRESS_CODE == code) {
          rel = value.ADDRESS_DESC;
        }
      });
      return rel;
    }

    $scope.getContactsForAddress = function(addrseq, addrtypecode) {
      var data = [];
      angular.forEach($scope.addressdata.primaryphones, function(value, key) {
        if (value.ADDRESS_SEQ == addrseq && value.ADDRESS_TYPE_CODE == addrtypecode) {
          value.type = 1;
          data.push(value);
        }
      });
      angular.forEach($scope.addressdata.additionalphones, function(value, key) {
        if (value.ADDRESS_SEQ == addrseq && value.ADDRESS_TYPE_CODE == addrtypecode) {
          value.type = 2;
          value.toggleclass = "fa-chevron-right";
          $scope[value.ADDRESS_TYPE_CODE + value.ADDRESS_SEQ] = "fa-chevron-right";
          data.push(value);
        }
      });
      return data;
    }

    $scope.toggleContact = function(contact) {
      contact.toggle = !contact.toggle;
      if (contact.toggle) {
        contact.toggleclass = "fa-chevron-down";
      } else {
        contact.toggleclass = "fa-chevron-right";
      }
    }
  });


  $scope.deleteAddr = function(atypcode, seqno) {
    var postdata = "atypcode=" + atypcode + "&seqno=" + seqno;
    $rootScope.postAPI( "/banneraddressdelete", postdata,
      function(data) {
        if (data) {
          apprise("Address deleted successfully", {
            'verify': false,
            'textYes': "Ok"
          }, function(r) {
            $rootScope.$apply(function() {
              $scope.fetchaddr();
            });
          });
        }
      });
  }


  $scope.deleteAddress = function(atypcode, seqno) {
    var onConfirm = function(buttonIndex) {
      if (buttonIndex == 1) {
        $scope.deleteAddr(atypcode, seqno);
      }
    };
    if (window.device) {
      navigator.notification.confirm(
        'Are you sure you want to delete this Address?', // message
        onConfirm, // callback to invoke with index of button pressed
        'Address Delete', // title
        ['Yes', 'No'] // buttonLabels
      );
    } else {
      apprise("Are you sure you want to delete this Address?", {
        'verify': true,
        'textYes': "Yes",
        'textNo': "No"
      }, function(r) {
        if (r) {
          $scope.deleteAddr(atypcode, seqno);
        }
      });
    }
  }


  $scope.deleteAddlCt = function(contact, address) {
    var postdata = "seqno=" + address.ADDRESS_SEQ + "&";
    postdata += "atypcode=" + address.ADDRESS_TYPE_CODE + "&";
    postdata += "fromdate=" + (address.FROM_DATE == null ? "" : address.FROM_DATE) + "&";
    postdata += "todate=" + (address.TO_DATE == null ? "" : address.TO_DATE) + "&";
    postdata += "housenumber=" + (address.HOUSE_NUMBER == null ? "" : address.HOUSE_NUMBER) + "&";
    postdata += "streetline1=" + (address.STREET_LINE1 == null ? "" : address.STREET_LINE1) + "&";
    postdata += "streetline2=" + (address.STREET_LINE2 == null ? "" : address.STREET_LINE2) + "&";
    postdata += "streetline3=" + (address.STREET_LINE3 == null ? "" : address.STREET_LINE3) + "&";
    postdata += "streetline4=" + (address.STREET_LINE4 == null ? "" : address.STREET_LINE4) + "&";
    postdata += "city=" + (address.CITY == null ? "" : address.CITY) + "&";
    postdata += "statecode=" + (address.STATE_CODE == null ? "" : address.STATE_CODE) + "&";
    postdata += "countycode=" + (address.COUNTY_CODE == null ? "" : address.COUNTY_CODE) + "&";
    postdata += "nationcode=" + (address.NATION_CODE == null ? "" : address.NATION_CODE) + "&";
    postdata += "zip=" + (address.ZIP == null ? "" : address.ZIP) + "&";
    if (address.primaryphone) {
      postdata += "phoneareacode=" + (address.primaryphone.AREA_CODE == null ? "" : address.primaryphone.AREA_CODE) + "&";
      postdata += "phonenumber=" + (address.primaryphone.PHONE_NUMBER == null ? "" : address.primaryphone.PHONE_NUMBER) + "&";
      postdata += "phoneextn=" + (address.primaryphone.EXTENSION == null ? "" : address.primaryphone.EXTENSION) + "&";
      postdata += "phonecountrycode=" + (address.primaryphone.COUNTRY_CODE == null ? "" : address.primaryphone.COUNTRY_CODE) + "&";
      postdata += "intlaccess=" + (address.primaryphone.INTERNATIONAL_ACCESS == null ? "" : address.primaryphone.INTERNATIONAL_ACCESS) + "&";
      postdata += "unlistedind=" + (address.primaryphone.UNLISTED_IND == null ? "" : address.primaryphone.UNLISTED_IND) + "&";
    } else {
      postdata += "phoneareacode=" + "" + "&";
      postdata += "phonenumber=" + "" + "&";
      postdata += "phoneextn=" + "" + "&";
      postdata += "phonecountrycode=" + "" + "&";
      postdata += "intlaccess=" + "" + "&";
      postdata += "unlistedind=" + "" + "&";
    }

    var addlseqnos = "";
    var addltelecodes = "";
    var addlcountrycodes = "";
    var addlareacodes = "";
    var addlphonenumbers = "";
    var addlphoneextns = "";
    var addlintlaccess = "";
    var addlunlistedinds = "";
    var addldelinds = "";
    angular.forEach(address.additionalphones, function(value, key) {
      if (key == 0) {
        addlseqnos += value.PHONE_SEQ;
        addltelecodes += value.PHONE_CODE;
        addlcountrycodes += value.COUNTRY_CODE;
        addlareacodes += value.AREA_CODE;
        addlphonenumbers += value.PHONE_NUMBER;
        addlphoneextns += value.EXTENSION;
        addlintlaccess += value.INTERNATIONAL_ACCESS;
        addlunlistedinds += value.UNLISTED_IND;
        if (value == contact) {
          addldelinds += "Y";
        }
      } else {
        addlseqnos += "," + value.PHONE_SEQ;
        addltelecodes += "," + value.PHONE_CODE;
        addlcountrycodes += "," + value.COUNTRY_CODE;
        addlareacodes += "," + value.AREA_CODE;
        addlphonenumbers += "," + value.PHONE_NUMBER;
        addlphoneextns += "," + value.EXTENSION;
        addlintlaccess += "," + value.INTERNATIONAL_ACCESS;
        addlunlistedinds += "," + value.UNLISTED_IND;
        if (value == contact) {
          addldelinds += ",Y";
        } else {
          addldelinds += ",";
        }
      }
    });
    postdata += "addlseqnos=" + addlseqnos + "&";
    postdata += "addltelecodes=" + addltelecodes + "&";
    postdata += "addlcountrycodes=" + addlcountrycodes + "&";
    postdata += "addlareacodes=" + addlareacodes + "&";
    postdata += "addlphonenumbers=" + addlphonenumbers + "&";
    postdata += "addlphoneextns=" + addlphoneextns + "&";
    postdata += "addlintlaccess=" + addlintlaccess + "&";
    postdata += "addlunlistedinds=" + addlunlistedinds + "&";
    postdata += "addldelinds=" + addldelinds;

    $rootScope.postAPI( "/banneraddressupdate", postdata, function(data) {
      if (data) {
        apprise("Contact deleted successfully", {
          'verify': false,
          'textYes': "Ok"
        }, function(r) {
          $rootScope.$apply(function() {
            $scope.fetchaddr();
          });

        });
      }
    });
  }

  $scope.deleteAddlContact = function(contact, address) {
    var onConfirm = function(buttonIndex) {
      if (buttonIndex == 1) {
        $scope.deleteAddlCt(contact, address);
      }
    };
    if (window.device) {
      navigator.notification.confirm(
        'Are you sure you want to delete this Contact?', // message
        onConfirm, // callback to invoke with index of button pressed
        'Contact Delete', // title
        ['Yes', 'No'] // buttonLabels
      );
    } else {
      apprise("Are you sure you want to delete this Contact?", {
        'verify': true,
        'textYes': "Yes",
        'textNo': "No"
      }, function(r) {
        if (r) {
          $scope.deleteAddlCt(contact, address);
        }
      });
    }
  }

}

function pageprocessorEContactDetails(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  var index = $routeParams.index
  $scope.btnlabel = "Update Contact";
  if (index == -1) {
    var priority = $rootScope.emergencycontacts.length + 1;
    $scope.emergencycontact = {
      PRIORITY: priority,
      LAST_NAME: "",
      FIRST_NAME: "",
      MIDDLE_NAME: "",
      HOUSE_NUMBER: "",
      STREET_LINE1: "",
      STREET_LINE2: "",
      STREET_LINE3: "",
      STREET_LINE4: "",
      CITY: "",
      STATE_CODE: "",
      COUNTRY_CODE: "",
      ZIP: "",
      PHONE_AREA: "",
      PHONE_NUMBER: "",
      PHONE_EXTN: "",
      PHONE_COUNTRY_CODE: "",
      RELATIONSHIP_CODE: ""
    };
    $scope.btnlabel = "Add Contact";
  } else {
    var selectedContact = $rootScope.emergencycontacts[index];
    $scope.emergencycontact = selectedContact;
  }

  $scope.showalert = function(message, title) {
    if (window.device) {
      navigator.notification.alert(message, null, title, null);
    } else {
      apprise(message, {
        'verify': false,
        'textYes': "Ok"
      }, function(r) {});
    }
  }
  $scope.validateRequired = function() {
    if ($scope.emergencycontact.RELATIONSHIP_CODE == "" || $scope.emergencycontact.RELATIONSHIP_CODE == null) {
      $scope.showalert("Please select Relationship", "Emergency Contact");
      return false;
    }
    if (!$scope.emergencycontact.FIRST_NAME || $scope.emergencycontact.FIRST_NAME.trim() == "") {
      $scope.showalert("Please enter First name", "Emergency Contact");
      return false;
    } else {
      if (/^[a-zA-Z0-9- ]*$/.test($scope.emergencycontact.FIRST_NAME.trim()) == false) {
        $scope.showalert('Special characters (" , + - * / \ @ # $ % ^ &) should not be used in name.');
        return false;
      }
    }

    if (($scope.emergencycontact.MIDDLE_NAME != "" && $scope.emergencycontact.MIDDLE_NAME != null) &&
      /^[a-zA-Z0-9- ]*$/.test($scope.emergencycontact.MIDDLE_NAME.trim()) == false) {
      $scope.showalert('Special characters (" , + - * / \ @ # $ % ^ &) should not be used in name.');
      return false;
    }

    if (!$scope.emergencycontact.LAST_NAME || $scope.emergencycontact.LAST_NAME.trim() == "") {
      $scope.showalert("Please enter Last name", "Emergency Contact");
      return false;
    } else {
      if (/^[a-zA-Z0-9- ]*$/.test($scope.emergencycontact.LAST_NAME.trim()) == false) {
        $scope.showalert('Special characters (" , + - * / \ @ # $ % ^ &) should not be used in name.', "Emergency Contact");
        return false;
      }
    }

    if (($scope.emergencycontact.STREET_LINE1 != "" && $scope.emergencycontact.STREET_LINE1 != null) && /^[a-zA-Z0-9- #]*$/.test($scope.emergencycontact.STREET_LINE1.trim()) == false) {
      $scope.showalert('Special characters (" , + - * / \ @ # $ % ^ &) should not be used in address.', "Emergency Contact");
      return false;
    }
    if (($scope.emergencycontact.STREET_LINE2 != "" && $scope.emergencycontact.STREET_LINE2 != null) && /^[a-zA-Z0-9- #]*$/.test($scope.emergencycontact.STREET_LINE2.trim()) == false) {
      $scope.showalert('Special characters (" , + - * / \ @ # $ % ^ &) should not be used in address.', "Emergency Contact");
      return false;
    }
    if (($scope.emergencycontact.STREET_LINE3 != "" && $scope.emergencycontact.STREET_LINE3 != null) && /^[a-zA-Z0-9- #]*$/.test($scope.emergencycontact.STREET_LINE3.trim()) == false) {
      $scope.showalert('Special characters (" , + - * / \ @ # $ % ^ &) should not be used in address.', "Emergency Contact");
      return false;
    }
    if (($scope.emergencycontact.STREET_LINE4 != "" && $scope.emergencycontact.STREET_LINE4 != null) && /^[a-zA-Z0-9- #]*$/.test($scope.emergencycontact.STREET_LINE4.trim()) == false) {
      $scope.showalert('Special characters (" , + - * / \ @ # $ % ^ &) should not be used in address.', "Emergency Contact");
      return false;
    }
    if (($scope.emergencycontact.ZIP != "" && $scope.emergencycontact.ZIP != null) && /^[a-zA-Z0-9- ]*$/.test($scope.emergencycontact.ZIP.trim()) == false) {
      $scope.showalert('Special characters (" , + - * / \ @ # $ % ^ &) should not be used in zip code.', "Emergency Contact");
      return false;
    }
    if ($scope.emergencycontact.STREET_LINE1 && !$scope.emergencycontact.CITY) {
      $scope.showalert("City is required if Street Line1 is specified.", "Emergency Contact");
      return false;
    }
    if ($scope.emergencycontact.COUNTRY_CODE != "" && $scope.emergencycontact.COUNTRY_CODE != null) {
      var city = $scope.emergencycontact.CITY == null ? "" : $scope.emergencycontact.CITY;
      if (city == "" || city == null || city.trim() == "") {
        $scope.showalert("Please enter City", "Emergency Contact");
        return false;
      }
      if ($scope.emergencycontact.STREET_LINE1 == "" || $scope.emergencycontact.STREET_LINE1 == null) {
        $scope.showalert("Please enter Address line 1", "Emergency Contact");
        return false;
      }
      if ($scope.emergencycontact.PHONE_NUMBER == "" || $scope.emergencycontact.PHONE_NUMBER == null) {
        $scope.showalert("Please enter Phone number", "Emergency Contact");
        return false;
      }
    }
    if (($scope.emergencycontact.STREET_LINE1 || $scope.emergencycontact.STREET_LINE2 ||
        $scope.emergencycontact.STREET_LINE3 || $scope.emergencycontact.STREET_LINE4) && $scope.emergencycontact.CITY) {
      if (!$scope.emergencycontact.STATE_CODE && !$scope.emergencycontact.COUNTRY_CODE) {
        $scope.showalert("If Address and City are provided, then State or Nation is required.", "Emergency Contact");
        return false;
      }
    }
    if ($scope.emergencycontact.STATE_CODE && ($scope.emergencycontact.ZIP == "" || $scope.emergencycontact.ZIP == null)) {
      $scope.showalert("Zip code is required when State Code is provided. ", "Emergency Contact");
      return false;
    }
    return true
  }
  $scope.updateContact = function() {
    if ($scope.validateRequired() == false) {
      return;
    }
    var postdata = "priority=" + $scope.emergencycontact.PRIORITY + "&";
    postdata += "oldPriority=" + $scope.emergencycontact.PRIORITY + "&";
    postdata += "lastName=" + ($scope.emergencycontact.LAST_NAME == null ? "" : $scope.emergencycontact.LAST_NAME) + "&";
    postdata += "firstName=" + ($scope.emergencycontact.FIRST_NAME == null ? "" : $scope.emergencycontact.FIRST_NAME) + "&";
    postdata += "middleName=" + ($scope.emergencycontact.MIDDLE_NAME == null ? "" : $scope.emergencycontact.MIDDLE_NAME) + "&";
    postdata += "surnamePrefix=" + ($scope.emergencycontact.SURNAME_PREFIX == null ? "" : $scope.emergencycontact.SURNAME_PREFIX) + "&";
    postdata += "houseNumber=" + ($scope.emergencycontact.HOUSE_NUMBER == null ? "" : $scope.emergencycontact.HOUSE_NUMBER) + "&";
    postdata += "streetLine1=" + ($scope.emergencycontact.STREET_LINE1 == null ? "" : $scope.emergencycontact.STREET_LINE1) + "&";
    postdata += "streetLine2=" + ($scope.emergencycontact.STREET_LINE2 == null ? "" : $scope.emergencycontact.STREET_LINE2) + "&";
    postdata += "streetLine3=" + ($scope.emergencycontact.STREET_LINE3 == null ? "" : $scope.emergencycontact.STREET_LINE3) + "&";
    postdata += "streetLine4=" + ($scope.emergencycontact.STREET_LINE4 == null ? "" : $scope.emergencycontact.STREET_LINE4) + "&";
    postdata += "city=" + ($scope.emergencycontact.CITY == null ? "" : $scope.emergencycontact.CITY) + "&";
    postdata += "stateCode=" + ($scope.emergencycontact.STATE_CODE == null ? "" : $scope.emergencycontact.STATE_CODE) + "&";
    postdata += "countryCode=" + ($scope.emergencycontact.COUNTRY_CODE == null ? "" : $scope.emergencycontact.COUNTRY_CODE) + "&";
    postdata += "zipCode=" + ($scope.emergencycontact.ZIP == null ? "" : $scope.emergencycontact.ZIP) + "&";
    postdata += "phoneArea=" + ($scope.emergencycontact.PHONE_AREA == null ? "" : $scope.emergencycontact.PHONE_AREA) + "&";
    postdata += "phoneNumber=" + ($scope.emergencycontact.PHONE_NUMBER == null ? "" : $scope.emergencycontact.PHONE_NUMBER) + "&";
    postdata += "phoneExtn=" + ($scope.emergencycontact.PHONE_EXTN == null ? "" : $scope.emergencycontact.PHONE_EXTN) + "&";
    postdata += "phoneCountryCode=" + ($scope.emergencycontact.PHONE_COUNTRY_CODE == null ? "" : $scope.emergencycontact.PHONE_COUNTRY_CODE) + "&";
    postdata += "reltCode=" + $scope.emergencycontact.RELATIONSHIP_CODE;

    $rootScope.postAPI(pagedef.endpoint, postdata, function(data) {
      if (data) {
        apprise("Contact saved successfully", {
          'verify': false,
          'textYes': "Ok"
        }, function(r) {
          $rootScope.$apply(function() {
            $location.path("/app/EContact/EContact");
          });
        });
      }
    });
  }
}

function pageprocessorEContact(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  $scope.emergencycontactcodes = "";
  $scope.emergencycontacts = "";


  $rootScope.getAPI( pagedef.endpoint, function(data) {
    $scope.emergencycontactcodes = data;
    $rootScope.countrycodes = data.countrycodes;
    $rootScope.statecodes = data.statecodes;
    $rootScope.relationshipcodes = data.relationshipcodes;
    $scope.getCountry = function(code) {
      var country = "";
      angular.forEach($scope.emergencycontactcodes.countrycodes, function(value, key) {
        if (value.COUNTRY_CODE == code) {
          country = value.COUNTRY_DESC;
        }
      });
      return country;
    }
    $scope.getState = function(code) {
      var state = "";
      angular.forEach($scope.emergencycontactcodes.statecodes, function(value, key) {
        if (value.STATE_CODE == code) {
          state = value.STATE_DESC;
        }
      });
      return state;
    }
    $scope.getRelation = function(code) {
      var rel = "";
      angular.forEach($scope.emergencycontactcodes.relationshipcodes, function(value, key) {
        if (value.RELATIONSHIP_CODE == code) {
          rel = value.RELATIONSHIP_DESC;
        }
      });
      return rel;
    }

  });

  $rootScope.getAPI( "/banneremergencycontactfetch", function(data) {
    $scope.emergencycontacts = data.fetch;
    $rootScope.emergencycontacts = data.fetch;
  });

  $scope.deleteCt = function(priority) {

    $rootScope.postAPI( "/banneremergencycontactdelete", "priority=" + priority,
      function(data) {
        if (data) {
          apprise("Contact deleted successfully", {
            'verify': false,
            'textYes': "Ok"
          }, function(r) {
            $rootScope.$apply(function() {
              $rootScope.getAPI( "/banneremergencycontactfetch", function(data) {
                $scope.emergencycontacts = data.fetch;
                $rootScope.emergencycontacts = data.fetch;
              });
            });
          });
        }
      });
  }

  $scope.deleteContact = function(priority) {
    var onConfirm = function(buttonIndex) {
      if (buttonIndex == 1) {
        $scope.deleteCt(priority);
      }
    };
    if (window.device) {
      navigator.notification.confirm(
        'Are you sure you want to delete this contact?', // message
        onConfirm, // callback to invoke with index of button pressed
        'Emergency Contact', // title
        ['Yes', 'No'] // buttonLabels
      );
    } else {
      apprise("Are you sure you want to delete this contact?", {
        'verify': true,
        'textYes': "Yes",
        'textNo': "No"
      }, function(r) {
        if (r) {
          $scope.deleteCt(priority);
        }
      });
    }

  }
}

