  }


  $scope.directiondisplay = function(mode, slat, slng, elat, elng) {
    try {
      $scope.trackinginfo = "";
      var mapDisplay = document.getElementById("MapMapCanvas")
      var latlng = new google.maps.LatLng(slat, slng);
      var myOptions = {
        zoom: 18,
        center: latlng,
        mapTypeId: google.maps.MapTypeId.ROADMAP
      };

      var map = new google.maps.Map(mapDisplay, myOptions);

      var directionsDisplay;
      if (typeof directiondisplay == 'undefined') {
        directionsDisplay = new google.maps.DirectionsRenderer({
          suppressMarkers: true
        });
        directionsDisplay.setMap(map);
      }
      var icons = {
        start: new google.maps.MarkerImage(
          // URL
          'images/pin_2.png',
          // (image width,height)
          new google.maps.Size(27, 27),
          //Point for the Image
          new google.maps.Point(0, 0)
        ),
        end: new google.maps.MarkerImage(
          // URL
          'images/pin_1.png',
          // (image width,height)
          new google.maps.Size(33, 45),
          //Point for the Image
          new google.maps.Point(0, 0)
        )
      };

      try {
        var directionsService
        if (typeof directionsService == 'undefined') {
          directionsService = new google.maps.DirectionsService();
        }

        var start = new google.maps.LatLng(slat, slng);
        var end = new google.maps.LatLng(elat, elng);
        var travelModeOption = mode;
        var request = {
          origin: start,
          destination: end,
          travelMode: "",
          unitSystem: google.maps.UnitSystem.METRIC
        };

        switch (travelModeOption) {
          case "walk":
            {
              var usertravelMode = "WALKING";
              request.travelMode = google.maps.TravelMode.WALKING;
              break;
            }
          case "cycle":
            {
              var usertravelMode = "BICYCLING";
              request.travelMode = google.maps.TravelMode.BICYCLING;
              break;
            }
          case "drive":
            {
              var usertravelMode = "DRIVING";
              request.travelMode = google.maps.TravelMode.DRIVING;
              break;
            }
          default:
            {
              var usertravelMode = "WALKING";
              request.travelMode = google.maps.TravelMode.WALKING;
              break;
            }
        }
      } catch (e) {
        alert(e)
      }
      directionsService.route(request, function(result, status) {

        if (status == google.maps.DirectionsStatus.OK) {
          try {
            /*for (i in $scope.markers) {
                $scope.markers[i].setMap(null);
            }*/
            console.log("direction result", result);
            var miles = (result.routes[0].legs[0].distance.value) * 0.000621;
            var d = miles.toString();
            $scope.distmiles = d.slice(0, 6);
            $scope.duration = result.routes[0].legs[0].duration.text;
            $scope.$apply();
            console.log($scope.distmiles + " " + $scope.duration);
            var leg = result.routes[0].legs[0];
            makeMarker(leg.start_location, icons.start, result.routes[0].legs[0].start_address, map);
            makeMarker(leg.end_location, icons.end, result.routes[0].legs[0].end_address, map);

            directionsDisplay.setDirections(result);
            //$scope.$digest();

          } catch (e) {
            console.log('error', e);
          }

        } else {

          console.log("Directions");
          //navigator.notification.alert("Oops! Your device dont have GPS enabled " + status, function() {}, 'Alert', 'Ok');
          //  $scope.trackinginfo = "Unable to get GPS data. ( " + status + " )";
          $scope.trackinginfo = "Directions Not Available";
          $scope.distmiles = "N/D"
          $scope.duration = "N/D"

          $scope.$digest();
        }
      });

      function makeMarker(position, icon, title, map) {
        new google.maps.Marker({
          position: position,
          map: map,
          icon: icon,
          title: title
        });
      }
    } catch (e) {
      console.log(e)
    }
  }
  var sel = $rootScope.locationSelected;
  if (sel) {
    var campusLocations = $.parseJSON(pagedef.mapMarkers)[sel];
    var item = {
      "name": campusLocations.name,
      "clat": campusLocations.latitude,
      "clong": campusLocations.longitude,
      "zoom": "16",
      "markers": [{
        "lat": campusLocations.latitude,
        "long": campusLocations.longitude,
        "desc": campusLocations.name
      }]
    };
    $scope.campusLocation = item;
    if ($scope.campusLocation) {
      $scope.selectitem();
    }
  }

  try {
    $scope.openExtMaps = function() {
      var usertravelMode = "";
      if ($scope.travelmode == "Walk") {
        usertravelMode = "/data=!4m2!4m1!3e2";
      } else if ($scope.travelmode == "Bicycle") {
        usertravelMode = "/data=!4m2!4m1!3e1";
      } else if ($scope.travelmode == "Drive") {
        usertravelMode = "/data=!4m2!4m1!3e0"
      } else {
        usertravelMode = "/data=!4m2!4m1!3e2";
      }


      if ($rootScope.mylocation) {
        $.blockUI();
        var campLoc = $scope.selectedLocation;
        var campLat = campLoc.clat;
        var campLong = campLoc.clong;

        var curLat = $rootScope.mylocation.coords.latitude;
        var curLong = $rootScope.mylocation.coords.longitude;
        if ((window.device) && (window.device.platform == "iOS")) {
          //var mapLocationUrl = 'maps.apple.com/?ll=' + campLat + ',' + campLong;
          //var mapLocationUrl = "http://maps.apple.com/maps?saddr=Current+Location&daddr=" + campLat + "," + campLong + "&dirflg=d&t=m";
          var mapLocationUrl = "https://www.google.co.in/maps/dir/" + curLat + "," + curLong + "/" + campLat + "," + campLong + usertravelMode;
          $.unblockUI();
          window.open(encodeURI(mapLocationUrl), '_system', 'location=no');
        } else {

          var dirUrl = "https://www.google.co.in/maps/dir/" + curLat + "," + curLong + "/" + campLat + "," + campLong + usertravelMode;
          $.unblockUI();
          window.open(dirUrl, '_system', 'location=no');
        }
      } else {

        navigator.notification.alert("Unable to find your location. Please check your location settings");
      }

    };
  } catch (err) {
    //alert(JSON.stringify(err));
  }

}

