    var url = pagedef.url;

    if (window.device) {} else {
        url = "/websimulator/xml?url=" + pagedef.url;
    }

    var xhr = $.ajax({
        url: url,
        dataType: "xml",
        async: true,
        data: {},
        success: function(resp) {
            try {
                var results = $.xml2json(resp);
                console.log(results);

                $scope.Newsdata = results;
              $rootScope.Newsdata = results;
                $scope.$apply();
                $.unblockUI();
            } catch (E) {
                alert("Exception : " + E);
            }
        },
        error: function(e) {
            alert("Error : " + e);
            $.unblockUI();
